/*==================================
 tmpmonitor jquery
 allresult Ping / SNMP Trap
 ==================================*/

$(function () {
  $('#start').on('click', function () {

    // start ボタンを変更
    $('#start').text('Runnig...').addClass('button_running').addClass('pointer-events_none');
    window.open('summary');

    // 全 MonitorBox の IP 情報を配列に格納
    var tmp = $('#natips').text();
    tmp = tmp.replace(/\s+/g, "").replace(/\r?\n/g,"").split(",");
    var natips = $.grep(tmp, function(e){return e;});

    // console.log(natips);
    // console.log(ips.length);

    // 各 MonitorBox から Ping/SNMPTRAP 情報を取得し表示する処理
    $.each(natips, function(i, natip) {
        // console.log(natip);

        // Ping 関連の処理
        _createAllPingTable(natip, i);

        $.cookie("pingnow", "yes");

        var intervalId;
        intervalId = setInterval(function() {
          if ($.cookie('pingnow')) {

            var num_cookie = 'ping_processing' + i;
            if (!($.cookie(num_cookie))) {
              $.cookie(num_cookie, "yes");
              _postAllPingAjax(natip, i)
            };
          _createAllPingTable(natip, i);
          };

        // SNMP TRAP 関連の処理
        _getAllSnmptrapAjax(natip, i);

        }, 60000);

      // Ping を停止する処理を実行
      $('#stop').click(function () {
        $.removeCookie("pingnow");
        for (var i = 0; i < 50; i++) {
          var cookie_del = 'ping_processing' + i;
          $.removeCookie(cookie_del);
        }
        clearInterval(intervalId);
        $('#start').text('Start').removeClass('button_running').removeClass('pointer-events_none');
      });
    
    });
  });
});



function _createAllPingTable(natip, num)
{
  $.ajax({
    type: 'GET',
    url: 'http://' + natip + ':3000/script/ping.json',
    dataType: 'JSON',
    success: function (datas) {

      var results = '';
      $.each(datas, function(i, value) {

        // 直近3回分の Ping Status で1つでも Error があったホストのみを表示        
        if ($.inArray(0, value[8]) == 0 || $.inArray(0, value[8]) == 1 || $.inArray(0, value[8]) == 2) {
            var td = '<tr><td class="' + value[9] + ' ping_hostname' + num + '">' + value[0] + '</td><td class="' + value[9] + ' ping_ip' + num + '">' + value[1] + '</td><td class="' + value[9] + ' ping_status' + num + '">' + value[8] + '</td><td class="' + value[9] + ' ping_all' + num + '">' + value[2] + '</td><td class="' + value[9] + ' ping_ng' + num + '">' + value[3] + '</td><td class="' + value[9] + ' ping_ngpercent' + num + '">' + value[4] + '</td><td class="' + value[9] + ' ping_short' + num + '">' + value[5] + '</td><td class="' + value[9] + ' ping_long' + num + '">' + value[6] + '</td><td class="' + value[9] + ' ping_average' + num + '">' + value[7] + '</td></tr>'
            results += td;
        }
      });

      if (results == '') {
        results = '<tr><td colspan="9">直近でPingエラーは発生していません。</td></tr>';
      }

      var ids = 'tbody#ping_change_tbody' + num;
      $(ids).html(results);
    },
    complete: function () {
    }
  });
}


